scriptItem.scriptCode:
import os, re def main(): if hou.hipFile.path().startswith(os.path.join(os.path.expanduser('~'), 'untitled')): return regx = re.compile(r'[\w\.-]+[\.|_]((v?)(0+)(\d+))') dir, file = os.path.split(hou.hipFile.name()) name, ext = os.path.splitext(file) matches = regx.search(name)
if matches: wholeVersion = matches.group(1) vString = matches.group(2) zeroes = matches.group(3) num = matches.group(4)
nextVersionNum = int(num) + 1 nextVersionNum = str(nextVersionNum).zfill(len(zeroes) + len(num))
name = name.replace(wholeVersion, vString + nextVersionNum) else: # No version number added yet name += '.0001'
hou.hipFile.save(file_name=os.path.join(dir, '{}{}'.format(name, ext))) main()

import os
def main():
	if hou.hipFile.path().startswith(os.path.join(os.path.expanduser('~'), 'untitled')):
		hou.ui.displayMessage('Please save your file first', title='Default file location', severity=hou.severityType.Error)
		return
	sdmUploadNodes = [n for n in hou.node('/out').children() if n.type().name() == 'sdm_upload']
	node = ''
	if sdmUploadNodes:
		node = sdmUploadNodes[0]
	else:
		node = hou.node('/out').createNode('sdm_upload')
		node.moveToGoodPosition()

	net = hou.ui.paneTabOfType(hou.paneTabType.NetworkEditor)

	if net:
		net.setCurrentNode(node)
main()
